mapper.select:
	/*loginChk*/
		SELECT
		    COUNT(1)
		FROM cust_epis.dj_eval_commissioner A, cust_epis.dj_eval_committee B
		WHERE A.committee_seq = B.committee_seq
		  AND A.EVAL_ID = #{id}
		  AND REPLACE(A.EVAL_PHONE, '-', '') = #{phone}
		  AND B.TITLE =#{title}
		  AND ((A.ATTEND_YN = 'Y' AND A.EVAL_AVOID='N') OR (A.ATTEND_YN='N' AND A.EVAL_AVOID='Y'))
          AND A.ACTIVE = "Y"

SELECT A.COMMITTEE_SEQ, A.TITLE, A.REQ_DEPT_NAME, A.OPR_EMP_NAME_1, A.EVAL_E_DATE, A.STEP FROM cust_epis.dj_eval_committee A WHERE 1=1
AND DATE_FORMAT(A.EVAL_S_DATE, '%Y%m%d') = DATE_FORMAT(#{startDay}, '%Y%m%d')
AND A.STEP = 'A' ORDER BY A.EVAL_S_DATE DESC;
